CREATE TABLE IF NOT EXISTS `billing_tender` ( `tender_id` int(11) NOT NULL AUTO_INCREMENT, `number` int(11) DEFAULT NULL, `name` varchar(255) DEFAULT NULL, `description` varchar(255) DEFAULT NULL, `price` decimal(19,2) DEFAULT NULL, `opening_date` datetime DEFAULT NULL, `closing_date` datetime DEFAULT NULL, `created_date` datetime DEFAULT NULL, `retired` bit(1) DEFAULT NULL, `retired_date` datetime DEFAULT NULL, PRIMARY KEY (`tender_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8
CREATE TABLE IF NOT EXISTS `billing_company` ( `company_id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `address` varchar(255) DEFAULT NULL, `description` varchar(255) DEFAULT NULL, `phone` varchar(20) DEFAULT NULL, `created_date` datetime DEFAULT NULL, `retired` bit(1) DEFAULT NULL, `retired_date` datetime DEFAULT NULL, PRIMARY KEY (`company_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_ambulance` ( `ambulance_id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `description` varchar(255) DEFAULT NULL, `created_date` datetime DEFAULT NULL, `retired` bit(1) DEFAULT NULL, `retired_date` datetime DEFAULT NULL, PRIMARY KEY (`ambulance_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_driver` ( `driver_id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `address` varchar(255) DEFAULT NULL, `description` varchar(255) DEFAULT NULL, `phone` varchar(20) DEFAULT NULL, `created_date` datetime DEFAULT NULL, `retired` bit(1) DEFAULT NULL, `retired_date` datetime DEFAULT NULL, PRIMARY KEY (`driver_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_service` ( `service_id` int(11) NOT NULL auto_increment, `concept_id` int(11) default NULL, `name` varchar(255) default NULL, `short_name` varchar(255) default NULL, `price` decimal(19,2) default NULL, `disable` bit(1) default NULL, PRIMARY KEY (`service_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_ambulance_bill` ( `ambulance__bill_id` int(11) NOT NULL auto_increment, `description` varchar(255) default NULL, `creator` int(11) default NULL, `amount` decimal(19,2) default NULL, `printed` bit(1) default NULL, `created_date` datetime default NULL, `voided` bit(1) default NULL, `voided_date` datetime default NULL, `driver_id` int(11) default NULL, PRIMARY KEY (`ambulance__bill_id`), KEY `fk_ambulancebill_driver` (`driver_id`), KEY `fk_ambulancebill_user` (`creator`), CONSTRAINT `fk_ambulancebill_user` FOREIGN KEY (`creator`) REFERENCES `users` (`user_id`), CONSTRAINT `fk_ambulancebill_driver` FOREIGN KEY (`driver_id`) REFERENCES `billing_driver` (`driver_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_ambulance_bill_item` ( `ambulance__bill_item_id` int(11) NOT NULL auto_increment, `ambulance_id` int(11) default NULL, `ambulance_bill_id` int(11) default NULL, `number_of_trip` int(11) default NULL, `amount` decimal(19,2) default NULL, `name` varchar(255) default NULL, `created_date` datetime default NULL, `voided` bit(1) default NULL, `voided_date` datetime default NULL, PRIMARY KEY (`ambulance__bill_item_id`), KEY `fk_ambulancebillitem_ambulancebill` (`ambulance_bill_id`), KEY `fk_ambulancebillitem_ambulance` (`ambulance_id`), CONSTRAINT `fk_ambulancebillitem_ambulance` FOREIGN KEY (`ambulance_id`) REFERENCES `billing_ambulance` (`ambulance_id`), CONSTRAINT `fk_ambulancebillitem_ambulancebill` FOREIGN KEY (`ambulance_bill_id`) REFERENCES `billing_ambulance_bill` (`ambulance__bill_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_patient_service_bill_item` ( `patient_service_bill_item_id` int(11) NOT NULL auto_increment, `service_id` int(11) default NULL, `patient_service_bill_id` int(11) default NULL, `unitPrice` decimal(19,2) default NULL, `amount` decimal(19,2) default NULL, `quantity` int(11) default NULL, `name` varchar(255) default NULL, `created_date` datetime default NULL, `voided` bit(1) default NULL, `voided_date` datetime default NULL, PRIMARY KEY (`patient_service_bill_item_id`), KEY `fk_patientservicebillitem_service` (`service_id`), KEY `fk_patientservicebillitem_patientservicebill` (`patient_service_bill_id`), CONSTRAINT `fk_patientservicebillitem_patientservicebill` FOREIGN KEY (`patient_service_bill_id`) REFERENCES `billing_patient_service_bill` (`patient_service_bill_id`), CONSTRAINT `fk_patientservicebillitem_service` FOREIGN KEY (`service_id`) REFERENCES `billing_service` (`service_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `billing_tender_bill` ( `tender_bill_id` int(11) NOT NULL auto_increment, `description` varchar(255) default NULL, `creator` int(11) default NULL, `amount` decimal(19,2) default NULL, `printed` bit(1) default NULL, `created_date` datetime default NULL, `voided` bit(1) default NULL, `voided_date` datetime default NULL, `company_id` int(11) default NULL, PRIMARY KEY (`tender_bill_id`), KEY `fk_tenderbill_company` (`company_id`), KEY `fk_tenderbill_user` (`creator`), CONSTRAINT `fk_tenderbill_user` FOREIGN KEY (`creator`) REFERENCES `users` (`user_id`), CONSTRAINT `fk_tenderbill_company` FOREIGN KEY (`company_id`) REFERENCES `billing_company` (`company_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
